import { Component } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { useRef, useEffect  } from 'react';
import './Chat.css';

const client = new W3CWebSocket('ws://localhost:8080/chat');
// const client = new W3CWebSocket('wss://chat-server-spring.herokuapp.com/chat');

const messages = [
  // {id:"1", text:"Dummy message 1"},
  // {id:"2", text:"Dummy message 2"}
];

const AlwaysScrollToBottom = () => {
  const elementRef = useRef();
  useEffect(() => elementRef.current.scrollIntoView());
  return <div ref={elementRef} />;
};

class Chat extends Component{

  constructor(props) {
    super(props);
    this.state = { messages: messages, message: {from:"Ignazio",text:""} };
  }

  handleChange(e) {
    this.setState({
      message: {
        ...this.state.message, // keep all other key-value pairs
        text: e.target.value, // update the value of specific key
      },
    });
  }

  // Manda il messaggio salvato sullo state e lo azzera
  sendMessage = () => {
    let newMsg = {
      from: this.state.message.from,
      message: this.state.message.text
    };
    client.send(newMsg);
    this.setState({
      message: {
        ...this.state.message, 
        text: "",
      },
    });
  }

  componentDidMount() {
    client.onopen = () => {
      console.log('WebSocket Client Connected');
    };
    client.onmessage = (message) => {
      console.log(JSON.stringify(message.data));
      let id = this.state.messages.length;
      this.setState({
        messages: [...this.state.messages, {id:id+1,text: message.data}]
      })
    };
    client.onclose = () => {
      console.log('WebSocket connection closed');
    };
  }

  render() {
    return (
      <div>
        <h2>Chat realizzata con React e WebSockets</h2>
        <div className="chatbox">
          <div id="messages" className="messages">
            {this.state.messages.map((message) => {
              return (
                <div className="message-text" key={message.id}>
                  {message.from} : {message.text}
                </div>
              );
            })}
            <AlwaysScrollToBottom />
          </div>
          <div className="message-input">
            <input
              className="message-input-text"
              value={this.state.message.text || ""}
              onChange={this.handleChange.bind(this)}
            ></input>
            <button
              className="message-input-send"
              onClick={() => this.sendMessage()}
            >
              Send
            </button>
          </div>
        </div>
      </div>
    );
  }

}

export default Chat;


