import React from 'react'
import { useState, useEffect } from 'react';

function ClockFunc() {
	const [clock, setClock] = useState("");

	useEffect(() => {
		setInterval(() => {
		setClock(clock => new Date().toLocaleTimeString());
		}, 1000);
	}, []);

	return (
		<div>
			<h1>Good morning!</h1>
      		<h2>Sono le {clock}</h2>
		</div>
	)
}

export default ClockFunc

